import styled from '@emotion/styled'

export const FriendItems = styled.ul`
display: flex;
flex-direction: column;
margin: 0 auto;
width: 150px;
gap: 7px;
`
export const Card = styled.li`
background-color: lightgray;
    box-shadow: 0px 1px 6px 4px grey;
    display: flex;
`
